import React, { Component } from 'react'
import Profil from './components/Profil'




class App extends Component {

    state = {
        person : {
                   imgSrc:     '/images/07.jpg',
                   fullName:   'touati karim',
                   bio:        'im a programmer',
                   profession: 'programmer',
                   show: true,
                 }

            }

  showFunction = () => {
       this.setState({
         show : !this.state.person.show

       });

  }



   render(){
     if(this.state.person.show){
         return  <div>
                       <button onClick={this.showFunction}>show Profil</button>
                       <Profil info={this.state.person} />
                   </div>
     }else{
        return 
              <div className='text-center m-5 auto'>

                {this.state.person.show && 
                     <button onClick={this.showFunction}>show Me</button> 
                 }
                 </div>

     }



     }

      }



export default App


