import Profil from './components/Profil'


class App extends Component {
  constructor(){
      super()
            this.state = {
        person : {
                  imgSrc :     '/images/07.jpg',
                  fullName :   'touati karim',
                  bio :        'im a programmer',
                  profession : 'programmer'},
                  show : true,
                  counter : 0,
            }
       this.showMe = this.showMe.bind(this)     
  }



     showMe = () => {
         const newShow = this.state.person[1].show  ? <Profil coordonee={this.state.person} /> : null;
         this.setState({
             newShow
         })

     }



   render(){
        return(
            <div className='container'>

                <button onClick={this.showMe}>show Me</button>
            </div>
        )
      }
}


export default App
