import React, {Component} from 'react'



class Profil extends Component{
  constructor(){
    super();
          this.state = { counter : 0 }

        this.clickHandler = this.clickHandler.bind(this);
  }


  clickHandler(){
        this.setState({counter : this.state});
        
      }


  render(){
     return (
       <div className='container' style={{width:'30%'}}>

         <form className='text-center'>
           <button className='btn btn-primary btn-lg' onClick={this.clickHandler}>Click Here</button>
           <h1>{this.state.counter}</h1>
         </form>





           <div className='card card-body mb-3'>
                 <div>
                    <img src={this.props.info.imgSrc} alt='logo' style={{maxWidth:'100%'}} />
                 </div>
                 <ul className='list-group'>
                    <li className='list-group-item'>
                      {this.props.info.FullName}
                    </li>
                    <li className='list-group-item'>
                      {this.props.info.profession}
                    </li>
                    <li className='list-group-item'>
                      {this.props.info.bio}
                    </li>
                 </ul>
           </div>
       </div>
  );
}
}

export default Profil
