import React, { Component } from 'react'
import Profil from './Profil'
import info from './Info'


class App extends Component {
  constructor(){
    super()
          this.state = { info : info ,
                         member : [],
                         show   : true,
                       }
            this.clickHandler = this.clickHandler.bind(this);
               }


  clickHandler(){
        this.state.member.push(this.state.info[this.state.show])
        this.setState({show : !this.state.show})
  }


    render(){
      return(
         <div>
            <form>
                   <button className='btn btn-primary btn-md-12' onClick={this.clickHandler}>Click Me</button>
                 </form>
           <Profil />
           {this.clickHandler(this.state.member)}

         </div>
      )
    }
}


  export default App
