import React, { Component } from 'react'
import Profil from './components/Profil'


class App extends Component {

        state = {
             imgSrc:     '/images/07.jpg',
             fullName:   'touati karim',
             bio:        'im a proffesionel boxer',
             profession: 'boxer',
             show: false,
            }


  HandleShow = () => {
    this.setState({
       show : !this.state.show
    })
  };



  render(){

    const { show , state } = this.state;

    const showMe = show ? ('cacher profil') : ('afficher profil');

    return(
      <div className='text-center'>
          <button className='btn btn-danger mt-5' onClick={this.HandleShow}>{showMe}</button>
          {show ? <Profil info={state} /> : null}

      </div>
    )
  }
}

export default App
