                    profession: 'programmer' }
    }




  render(){
     return (
       <div className='container' style={{width:'30%'}}>
           <div className='card'>



               <h1>{this.state.fullName}</h1>
               <h4>{this.state.profession}</h4>
               <p>{this.state.bio}</p>


           </div>
           <botton className='btn btn-primary' onClick={this.clickEvent}>Click Me</botton>
       </div>
  );
}
}


export default App;
