import React, { Component } from 'react'
import Profil from './components/Profil'




class App extends Component {

    state = {
             imgSrc:     '/images/07.jpg',
             fullName:   'touati karim',
             bio:        'im a proffesionel boxer',
             profession: 'boxer',
             show: true,
             counter: 0,
             title: '',
            }



  componentDidUpdate(){
     document.title = `you have clicked ${this.state.counter +1} time`
  }      


  counterUp = () =>{
    this.setState({
      counter: this.state.counter +1
    });    
  }

  showFunction = () => {
       this.setState({
         show : !this.state.show

       });

  }



   render(){
     return(
          <div className='text-center'>
            <button className='btn btn-danger mt-5 ' onClick={this.showFunction}>Sohw profil</button>
            <button className='btn btn-danger mt-5 ' onClick={this.counterUp}>{this.state}</button>
            {this.state.show ? 
               <div><Profil name={this.state}/></div>
              : 
               null}
          </div>
     )
      }
    }


export default App




