import React, {Component} from 'react'



class Profil extends Component{
  constructor(){
    super()
          this.state = {
                        counter : 0,
          }
        this.clickHandle = this.clickHandle.bind(this);
  }


  clickHandle(prevState){
        this.setState({counter : prevState.counter +1})
        
      }


  render(){
     return (
       <div className='container' style={{width:'30%'}}>
         <button className='btn btn-primary btn-fluid ' onClick={this.clickHandle}>Click Here</button>
         <h1>{counter}</h1>





           <div className='card card-body mb-3'>
                 <div>
                    <img src={this.props.info.imgSrc} alt='logo' style={{maxWidth:'100%'}} />
                 </div>
                 <ul className='list-group'>
                    <li className='list-group-item'>
                      {this.props.info.FullName}
                    </li>
                    <li className='list-group-item'>
                      {this.props.info.profession}
                    </li>
                    <li className='list-group-item'>
                      {this.props.info.bio}
                    </li>
                 </ul>
           </div>
       </div>
  );
}
}

export default Profil
